insert.foreach:
INTO
	   TICKET_PAYMENT
  	   (
  	    ORDER_NO
  	   ,MEM_NO
  	   ,TICKET_NO
  	   ,COUNT
  	   ,PAYMENT_AMOUNT
  	   )
VALUES
	   (

	   	   SEQ_TICKET_ORDER.NEXTVAL


	       SEQ_TICKET_ORDER.CURRVAL

	   ,#{ item.memNo }
	   ,#{ item.ticketNo }
	   ,#{ item.count }
	   ,#{ item.paymentAmount }
	   )

mapper.insert:
INSERT INTO ACCOMM_ATTACHMENT ( FILE_NO, ACCOMM_NO, ORIGIN_NAME, CHANGE_NAME, FILE_PATH, UPLOAD_DATE ) SELECT SEQ_ACCFNO.NEXTVAL, SEQ_ACCNO.CURRVAL, A.* FROM (
SELECT #{item.originName} AS ORIGIN_NAME, #{item.changeName} AS CHANGE_NAME, #{item.filePath} AS FILE_PATH, SYSDATE AS UPLOAD_DATE FROM DUAL
) A

INSERT INTO ACTIVITY_ATTACHMENT ( FILE_NO, ACTIVITY_NO, ORIGIN_NAME, CHANGE_NAME, FILE_PATH, UPLOAD_DATE ) SELECT SEQ_ACTFNO.NEXTVAL, SEQ_ACTNO.CURRVAL, A.* FROM (
SELECT #{item.originName} AS ORIGIN_NAME, #{item.changeName} AS CHANGE_NAME, #{item.filePath} AS FILE_PATH, SYSDATE AS UPLOAD_DATE FROM DUAL
) A

INSERT INTO ROOM_ATTACHMENT ( FILE_NO, ROOM_NO, ORIGIN_NAME, CHANGE_NAME, FILE_PATH, UPLOAD_DATE ) SELECT SEQ_RMFNO.NEXTVAL, SEQ_RMNO.CURRVAL, A.* FROM (
SELECT #{item.originName} AS ORIGIN_NAME, #{item.changeName} AS CHANGE_NAME, #{item.filePath} AS FILE_PATH, SYSDATE AS UPLOAD_DATE FROM DUAL
) A
